import React, { useState, useEffect } from "react";
import "./Search.css";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import MenuItem from "@mui/material/MenuItem";
import Select from "@mui/material/Select";
import InputLabel from "@mui/material/InputLabel";
import FormControl from "@mui/material/FormControl";

import API_BASE from "../ApiBase";

export default function Search() {
    const [searchQuery, setSearchQuery] = useState("");
    const [authorQuery, setAuthorQuery] = useState("");
    const [sortOrder, setSortOrder] = useState("recent");
    const [searchResults, setSearchResults] = useState([]);
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);
    const [page, setPage] = useState(1);
    const [hasMore, setHasMore] = useState(false);
    const limit = 6; // Items per page

    const handleSearch = async () => {
        if (!searchQuery.trim() && !authorQuery.trim()) return;

        setError("");
        setLoading(true);

        try {
            const response = await fetch(
                `${API_BASE}/manga/search?title=${searchQuery}&author=${authorQuery}&sort=${sortOrder}&page=${page}&limit=${limit}`
            );
            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || "Failed to fetch manga results.");
            }

            setSearchResults(data);
            setHasMore(data.length === limit);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // Re-run search on page/sort change
    useEffect(() => {
        handleSearch();
        // eslint-disable-next-line
    }, [page, sortOrder]);

    const handleNextPage = () => setPage((prev) => prev + 1);
    const handlePrevPage = () => setPage((prev) => Math.max(prev - 1, 1));

    return (
        <div className="search-container">
            <h2>Search for Manga</h2>
            <br />

            <TextField
                className="textfield"
                label="Search by Title"
                variant="standard"
                color="secondary"
                sx={{ input: { color: "white" }, mr: 2 }}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />

            <TextField
                className="textfield"
                label="Search by Author"
                variant="standard"
                color="secondary"
                sx={{ input: { color: "white" }, mr: 2 }}
                value={authorQuery}
                onChange={(e) => setAuthorQuery(e.target.value)}
            />

            <FormControl variant="standard" sx={{ minWidth: 120, mr: 2 }}>
                <InputLabel style={{ color: "white" }}>Sort By</InputLabel>
                <Select
                    value={sortOrder}
                    onChange={(e) => setSortOrder(e.target.value)}
                    label="Sort By"
                    sx={{ color: "white" }}
                >
                    <MenuItem value="recent">Recent</MenuItem>
                    <MenuItem value="asc">Title A–Z</MenuItem>
                    <MenuItem value="desc">Title Z–A</MenuItem>
                </Select>
            </FormControl>

            <Button
                variant="contained"
                color="success"
                onClick={() => {
                    setPage(1);
                    handleSearch();
                }}
                disabled={loading}
            >
                {loading ? "Searching..." : "Search"}
            </Button>

            <br /><br />
            {error && <p className="error">{error}</p>}

            {searchResults.length === 0 && !loading && (
                <p>No results found.</p>
            )}

            {searchResults.length > 0 && (
                <>
                    <div className="search-results">
                        {searchResults.map((manga) => (
                            <div key={manga.id} className="manga-item">
                                <img
                                    src={`${API_BASE}${manga.coverImage}`}
                                    alt={manga.title}
                                    className="manga-cover"
                                />
                                <div className="manga-details">
                                    <h3>{manga.title}</h3>
                                    <p>Author: {manga.author}</p>
                                </div>
                            </div>
                        ))}
                    </div>

                    <div className="pagination-controls">
                        <Button onClick={handlePrevPage} disabled={page === 1}>
                            Previous
                        </Button>
                        <span style={{ margin: "0 10px" }}>Page {page}</span>
                        <Button onClick={handleNextPage} disabled={!hasMore}>
                            Next
                        </Button>
                    </div>
                </>
            )}
        </div>
    );
}
